import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  const [currentTime, setCurrentTime] = useState(0);

  useEffect(() => {
    // thanks to proxy, requests goes first to
    // localhost:3000/time before it gets 
    // redirect to port 5000
    // if we don't use proxy we'd need configure CORS
    // since we'd issue the request over different origins
    fetch('api/time') 
      .then(res => res.json())
      .then(data => {
        setCurrentTime(data.time);
      });
  }, []); // the empty list makes useEffect run just 1 time

  return (
    <div className="App">
      <header className="App-header">
      <p>The current time is {currentTime}.</p>
      </header>
    </div>
  );
}

export default App;
